INSERT INTO `dichvu` (`id_dv`, `name_dv`, `image_dv`, `banner_dv`, `slogan_dv`, `mota_dv`, `Anhien`, `created_at`, `updated_at`, `slug`) VALUES
(1, 'THIẾT KẾ NỘI THẤT CÀ PHÊ', '4.jpg', 'bg-cafe.jpg', 'CHUYÊN TƯ VẤN VÀ CUNG CẤP GIẢI PHÁP THIẾT KẾ KHÔNG GIAN CAFE ẤN TƯỢNG, HỢP XU HƯỚNG', 'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum', 1, '2020-09-24 08:51:34', '2020-09-27 11:35:58', 'thiet-ke-noi-that-ca-phe'),
(3, 'THIẾT KẾ  NỘI THẤT VĂN PHÒNG', '1.jpg', 'bg-vanphong.jpg', 'TƯ VẤN THIẾT KẾ THI CÔNG VĂN PHÒNG TRỌN GÓI - NÂNG CAO HIỆU SUẤT LAO ĐỘNG', 'Thiết kế văn phòng đẹp không những thôi thúc tinh thần làm việc của nhân viên mà còn tạo được lòng tin tuyệt đối từ phía khách hàng và đối tác. Đến với Tramdecor, chúng tôi sẽ mang đến cho bạn những giải pháp thiết kế giúp cho văn phòng làm việc tràn đầy sức sống.', 1, '2020-09-24 02:55:30', '2020-09-27 11:39:49', 'thiet-ke-noi-that-van-phong'),
(4, 'THIẾT KẾ  NỘI THẤT NHÀ Ở', '1.jpg', 'bg-nha.jpg', 'Tramdecor Không Cho Phép Mình Đưa Ra Những Thành Phẩm Kém Chất Lượng, Bởi Vì Đó Như Là Một Yếu Tố Quyết Định Sự Thành Công Và Nhận Được Sự Tin Tưởng Từ Khách Hàng', 'Nhà là nơi chứng kiến cuộc sống thường nhật, là nơi giúp tâm hồn trở lên thanh thản sau giờ làm việc. Thiết kế nhà ở không đơn thuần chỉ dừng lại ở sở thích của gia chủ, mà còn hướng đến công năng và giá trị cảm xúc. Hãy chọn cho mình một hướng đi thuyết phục, còn những giá trị thuộc về không gian, Tramdecor sẽ giúp bạn thực hiện.\r\n\r\nTham khảo thêm dịch vụ HomeStylist của Tramdecor tại website : https://www.homestylistvn.com/', 1, '2020-09-24 02:59:55', '2020-09-27 11:39:57', 'thiet-ke-noi-that-nha-o'),
(6, 'THIẾT KẾ  NỘI THẤT CỬA HÀNG', 'cuahang.jpg', 'bg-cuahang.jpg', 'THIẾT KỂ CỬA HÀNG HIỆU QUẢ - TĂNG HIỆU SUẤT BÁN HÀNG TRAMDECOR MANG ĐẾN GIẢI PHÁP TIỆN ÍCH CHO MỌI KHÔNG GIAN CỬA HÀNG', '<p><span style=\"color: rgba(0, 0, 0, 0.85); font-family: Quicksand; font-size: 15.6795px;\">Thiết kế cửa hàng đẹp, ấn tượng sẽ khiến cho các sản phẩm trưng bày trở nên hấp dẫn và có giá trị hơn trong mắt của khách hàng, và chắc chắn đó cũng là lí do để thu hút khách hàng quay lại lần tiếp theo. Tramdecor là công ty chuyên thiết kế thi công nội thất cửa hàng uy tín tại HCM, chúng tôi luôn sẵn sàng trong hành trình tạo điểm nhấn cho không gian của bạn</span><br></p>', 1, '2020-09-27 00:36:08', '2020-09-27 11:40:02', 'thiet-ke-noi-that-cua-hang'),
(7, 'THIẾT KẾ NỘI THẤT QUÁN TRÀ SỮA', '1.jpg', 'bg-trasua.jpg', 'Tramdecor Không Cho Phép Mình Đưa Ra Những Thành Phẩm Kém Chất Lượng, Bởi Vì Đó Như Là Một Yếu Tố Quyết Định Sự Thành Công Và Nhận Được Sự Tin Tưởng Từ Khách Hàng.', '<p><span style=\"color: rgba(0, 0, 0, 0.85); font-family: Quicksand; font-size: 15.6795px;\">Mọi thương hiệu nổi bật đều xuất phát ấn tượng với không gian nội thất được đầu tư một cách chỉn chu. Việc đầu tư thiết kế quán trà sữa ngay từ ban đầu sẽ quyết định đến thẩm mỹ cũng như công năng sử dụng, mang đến hiệu quả kinh doanh cao. Tramdecor</span><strong style=\"font-weight: bold; color: rgba(0, 0, 0, 0.85); font-family: Quicksand; font-size: 15.6795px;\">&nbsp;</strong><span style=\"color: rgba(0, 0, 0, 0.85); font-family: Quicksand; font-size: 15.6795px;\">chuyên cung cấp dịch vụ thiết kế thi công nội thất quán trà sữa trọn gói, tạo nên những không gian sang trọng và đẳng cấp.</span><br></p>', 1, '2020-09-27 11:21:23', '2020-09-27 11:40:38', 'thiet-ke-noi-that-quan-tra-sua'),
(8, 'THIẾT KẾ NỘI THẤT NHÀ HÀNG', '1.jpg', 'bg-nhahang.jpg', 'Không Gian Chỉn Chu, Chuyên Nghiệp Với Ý Tưởng Độc Đáo Là Bước Khởi Đầu Hoàn Hảo Cho Hoạt Động Kinh Doanh Nhà Hàng Hiệu Quả', '<p><span style=\"color: rgba(0, 0, 0, 0.85); font-family: Quicksand; font-size: 15.6795px;\">Thiết kế nhà hàng đẹp không chỉ ảnh hưởng đến hình ảnh thương hiệu mà còn góp phần quan trọng tạo nên thành công lớn cho việc kinh doanh, giúp thu hút và giữ chân khách hàng. Tuy nhiên, để có được chỗ đứng nhất định trong thị trường F&amp;B, chủ đầu tư cần phải lựa chọn đơn vị tư vấn thiết kế nhà hàng có kinh nghiệm thực tế và am hiểu sâu sắc về lĩnh vực này. Tramdecor hy vọng sẽ đồng hành cùng chủ đầu tư hiện thực hóa ý tưởng kinh doanh của mình</span><br></p>', 1, '2020-09-27 11:26:21', '2020-09-27 11:26:21', 'thiet-ke-noi-that-nha-hang');

